import React, { useState } from 'react';
import axios from 'axios';

function TrackExercise() {
  const [exerciseData, setExerciseData] = useState({
    name: '',
    weight: '',
    repetitions: '',
  });

  const handleChange = (e) => {
    setExerciseData({ ...exerciseData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        'http://localhost:8000/api/exercises/',  // Replace with your backend API URL
        exerciseData
      );
      console.log('Exercise tracked successfully:', response.data);
      // Optionally, update the UI or redirect to another page
    } catch (error) {
      console.error('Exercise tracking error:', error);
    }
  };

  return (
    <div>
      <h2>Track Exercise</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Exercise Name"
          onChange={handleChange}
          value={exerciseData.name}
        />
        <input
          type="number"
          name="weight"
          placeholder="Weight (in kg)"
          onChange={handleChange}
          value={exerciseData.weight}
        />
        <input
          type="number"
          name="repetitions"
          placeholder="Repetitions"
          onChange={handleChange}
          value={exerciseData.repetitions}
        />
        <button type="submit">Track Exercise</button>
      </form>
    </div>
  );
}

export default TrackExercise;
