import React, { useEffect } from 'react';
import { Line } from 'react-chartjs-2';

function ExerciseGraph({ exerciseData }) {
  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false, // Allows the chart to resize within its container
    scales: {
      x: {
        type: 'time', // If your x-axis represents dates or timestamps
        time: {
          unit: '', // Adjust this based on your data granularity
        },
        title: {
          display: true,
          text: 'Date', // X-axis label
        },
      },
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Volume', // Y-axis label
        },
      },
    },
  };

  return (
    <div>
      <h3>Exercise Volume Over Time</h3>
      <Line data={exerciseData} options={chartOptions} />
    </div>
  );
}

export default ExerciseGraph;
