import React, { useRef, useEffect } from 'react';
import { Chart, TimeScale, CategoryScale } from 'chart.js'; // Import Chart from chart.js
import 'chartjs-adapter-date-fns';

Chart.register(TimeScale, CategoryScale);

function ExerciseGraph({ exerciseData }) {
  const chartRef = useRef(null);

  useEffect(() => {
    
    // Check if exerciseData is available
    if (exerciseData.length === 0) {
      return;
    }
    // Fetch exercise data when the component mounts
  useEffect(() => {
    const fetchExerciseData = async () => {
      try {
        const response = await axios.get('http://localhost:8000/exercises/');
        const initialExerciseData = response.data;
        setExerciseData(initialExerciseData);
      } catch (error) {
        console.error('Error fetching exercise data:', error);
      }
    };

    fetchExerciseData();
  }, []); // The empty dependency array ensures that this effect runs only once on mount

    
    // Extract labels (dates or timestamps) and volume values from exerciseData
    const labels = exerciseData.map((exercise) => exercise.date); // Update this to the actual date property
    const volumes = exerciseData.map((exercise) => exercise.volume);

    const chartData = {
      labels: labels,
      datasets: [
        {
          label: 'Exercise Volume',
          data: volumes,
          fill: false,
          borderColor: 'rgba(75, 192, 192, 1)',
        },
      ],
    };

    const chartOptions = {
      responsive: true,
      maintainAspectRatio: false,
      scales: {
        x: {
          type: 'time', // Use 'time' as the scale type
          title: {
            display: true,
            text: 'Date',
          },
        },
        y: {
          beginAtZero: true,
          title: {
            display: true,
            text: 'Volume',
          },
        },
      },
    };

    // Check if the previous chart instance exists and destroy it
    if (chartRef.current) {
      chartRef.current.destroy();
    }

    try {
      // Create a new chart instance and store it in the ref
      const ctx = document.getElementById('myChart').getContext('2d');
      chartRef.current = new Chart(ctx, {
      type: 'line',
      data: chartData,
      options: chartOptions,
      });
    } catch (error) {
      console.error('Error creating chart:', error);
      // Handle the chart creation error here
    }
  }, [exerciseData]);


    


  return (
    <div>
      <h3>Exercise Volume Over Time</h3>
      <canvas id="myChart" width={400} height={400}></canvas>
    </div>
  );
}

export default ExerciseGraph;
