import React from 'react';
import { Line } from 'react-chartjs-2';

function ExerciseGraph({ totalVolumeData }) {
    // Check if totalVolumeData is undefined or null
    if (!totalVolumeData || totalVolumeData.length === 0) {
      return <div>No data to display</div>;
    }

    console.log(totalVolumeData);
  
  // Extract exercise names and volumes from totalVolumeData
  const exerciseNames = totalVolumeData.map((data) => data.name);
  const volumes = totalVolumeData.map((data) => data.volume);
  
  const data = {
    labels: exerciseNames,
    datasets: [
      {
        label: 'Total Volume',
        data: volumes,
        fill: false,
        backgroundColor: 'rgba(75,192,192,0.4)',
        borderColor: 'rgba(75,192,192,1)',
      },
    ],
  };

  const options = {
    scales: {
      x: {
        type: 'category',
        labels: exerciseNames,
      },
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <div>
      <h2>Exercise Graph</h2>
      <Line data={data} options={options} />
    </div>
  );
}

export default ExerciseGraph;
